import { createTheme } from '@mui/material/styles';

const theme = createTheme({
    palette: {
        primary: {
            main: '#B0E0E6',
        },
        secondary: {
            main: '#f50057',
        },
        background: {
            default: '#B0E0E6',
        },
    },
    typography: {
        fontFamily: '"Inter", "Arial", sans-serif',
        h1: {
            fontWeight: 700, // Bold weight
            fontSize: "3rem",
          },
          h2: {
            fontWeight: 600, // Semi-Bold weight
            fontSize: "2.5rem",
          },
          h3: {
            fontWeight: 600, // Semi-Bold weight
            fontSize: "2rem",
          },
          h4: {
            fontWeight: 500, // Medium weight
            fontSize: "1.75rem",
          },
          h5: {
            fontWeight: 500, // Medium weight
            fontSize: "1.5rem",
          },
          h6: {
            fontWeight: 500, // Medium weight
            fontSize: "1.25rem",
          },
          subtitle1: {
            fontWeight: 400, // Regular weight
            fontSize: "1rem",
          },
          subtitle2: {
            fontWeight: 400, // Regular weight
            fontSize: "0.875rem",
          },
          body1: {
            fontWeight: 400, // Regular weight
            fontSize: "1rem",
          },
          body2: {
            fontWeight: 400, // Regular weight
            fontSize: "0.875rem",
          },
          button: {
            fontWeight: 600, // Semi-Bold weight
            fontSize: "0.875rem",
          },
          caption: {
            fontWeight: 300, // Light weight
            fontSize: "0.75rem",
          },
          overline: {
            fontWeight: 300, // Light weight
            fontSize: "0.625rem",
          },
      },

});

export default theme;
