import React, { useState, useEffect, useContext } from 'react';
import { Box, Button, Dialog, DialogActions, DialogContent, DialogTitle, FormControl, InputLabel, Select, MenuItem, Card, CardActionArea, CardContent, Typography } from '@mui/material';

const Programs = () => {
    const [programs, setPrograms] = useState([]);

    useEffect(() => {
      fetch("/api/programs")
        .then((res) => res.json())
        .then((data) => {
          console.log("Received data:", data); // Log data here
          setPrograms(data);
        })
        .catch((err) => console.error("Fetch error:", err)); // Log error here
    }
    , []);

    useEffect(() => {
      console.log("Programs:", programs); // Log programs here
    }
    , [programs]);


    return (
      <Box>
        <h1>Programs</h1>
        {programs.length > 0 ? (

        <ul>
          {programs.map((program, index) => (
            <li key={index}>
              <strong>{program.name}</strong>
              <ul>
                <li>Organization: {program.organization}</li>
                <li>Description: {program.description}</li>
                <li>Field: {JSON.stringify(program.field)}</li>
                <li>Eligibility: {JSON.stringify(program.eligibility)}</li>
                <li>Type: {program.type}</li>
                <li>Cost: {program.cost}</li>
                <li>Salary: {program.salary}</li>
                <li>Location: {program.location}</li>
                <li>Virtual: {String(program.virtual)}</li>
                <li>Website: {program.website}</li>
                <li>Start Date: {program.startDate.toString()}</li>
                <li>End Date: {program.endDate.toString()}</li>
              </ul>
            </li>
          ))}
        </ul>
        ) : (
          <Typography variant="h6" component="div">
            No programs found.
          </Typography>
        )
}
      </Box>
    );
}

export default Programs;



