import React, { useState } from 'react';
import {
  Box,
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  FormControl,
  InputLabel,
  Select,
  MenuItem
} from '@mui/material';

const dummyPrograms = [
    {
        name: 'Program 11',
        eligibility: 'High School',
        field: 'Mathematics',
        virtual_in_person: 'Virtual',
        cost: '>$1000',
        season: 'Summer',
        website_url: 'https://program11.com',
        acceptance_rate: '87%',
        description: 'This is a description for Program 11.',
        app_deadline: '2023-5-10'
      },
      {
        name: 'Program 12',
        eligibility: 'Undergraduate',
        field: 'Mathematics',
        virtual_in_person: 'Virtual',
        cost: '$100-$500',
        season: 'Fall',
        website_url: 'https://program12.com',
        acceptance_rate: '48%',
        description: 'This is a description for Program 12.',
        app_deadline: '2023-4-3'
      },
      {
        name: 'Program 13',
        eligibility: 'Undergraduate',
        field: 'Engineering',
        virtual_in_person: 'Virtual',
        cost: '$100-$500',
        season: 'Fall',
        website_url: 'https://program13.com',
        acceptance_rate: '21%',
        description: 'This is a description for Program 13.',
        app_deadline: '2023-5-18'
      }];

function Programs() {
  const [open, setOpen] = useState(false);
  const [selectedProgram, setSelectedProgram] = useState(null);
  const [filters, setFilters] = useState({
    eligibility: '',
    field: '',
    virtual_in_person: '',
    cost: '',
    season: ''
  });

  const handleOpen = (program) => {
    setSelectedProgram(program);
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleFilterChange = (event) => {
    const { name, value } = event.target;
    setFilters((prevFilters) => ({ ...prevFilters, [name]: value }));
  };

  const filteredPrograms = dummyPrograms.filter((program) => {
    return Object.keys(filters).every((key) => {
      return !filters[key] || filters[key] === program[key];
    });
  });

  return (
    <Box>
      <Box>
        <FormControl variant="outlined">
          <InputLabel>Eligibility</InputLabel>
          <Select name="eligibility" value={filters.eligibility} onChange={handleFilterChange}>
            <MenuItem value=""><em>None</em></MenuItem>
            {/* Add other options here */}
          </Select>
        </FormControl>
        {/* Repeat for other filters */}
      </Box>

      <Box>
        {filteredPrograms.map((program, index) => (
          <Button key={index} onClick={() => handleOpen(program)}>
            {program.name}
          </Button>
        ))}
      </Box>

      <Dialog open={open} onClose={handleClose}>
        <DialogTitle>{selectedProgram?.name}</DialogTitle>
        <DialogContent>
          {/* Display program details here */}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>Close</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
}

export default Programs;
