import {
    Button,
    Container,
    Grid,
    TextField,
    FormControl,
    InputLabel,
    Select,
    MenuItem,
  } from '@mui/material';
  import React, { useState } from 'react';

  function AddProgram() {
    const [programData, setProgramData] = useState({
      name: '',
      organization: '',
      field: '',
      eligibility: {
        age: '',
        year: '',
      },
      type: '',
      cost: '',
      location: '',
      virtual: '',
      startDate: '',
      endDate: '',
      website: '',
      applicationDeadline: '',
      description: '',
    });

    const handleChange = (e) => {
      const { name, value } = e.target;
      setProgramData({
        ...programData,
        [name]: value,
      });
    };

    const handleSubmit = (e) => {
      e.preventDefault();
      console.log(programData);
      // Add your API call here
    };

    return (
      <Container>
        <form onSubmit={handleSubmit}>
          <Grid container spacing={3}>
            <Grid item xs={6}>
              <TextField
                required
                name="name"
                label="Program Name"
                variant="outlined"
                fullWidth
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={6}>
              <TextField
                required
                name="organization"
                label="Organization"
                variant="outlined"
                fullWidth
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={6}>
              <TextField
                required
                name="field"
                label="Field"
                variant="outlined"
                fullWidth
                onChange={handleChange}
              />
            </Grid>
            <Grid item xs={6}>
              <FormControl variant="outlined" fullWidth>
                <InputLabel>Type</InputLabel>
                <Select
                  name="type"
                  label="Type"
                  onChange={handleChange}
                >
                  <MenuItem value="Internship">Internship</MenuItem>
                  <MenuItem value="Research">Research</MenuItem>
                  <MenuItem value="Course">Course</MenuItem>
                </Select>
              </FormControl>
            </Grid>
            {/* Add more form fields as needed */}
            <Grid item xs={12}>
              <Button type="submit" variant="contained" color="primary">
                Submit
              </Button>
            </Grid>
          </Grid>
        </form>
      </Container>
    );
  }

  export default AddProgram;
