import React from "react";
import * as XLSX from 'xlsx';

const UploadSpreadsheet = () => {
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    const reader = new FileReader();
    reader.onload = (evt) => {
      const bstr = evt.target.result;
      const wb = XLSX.read(bstr, { type: "binary" });
      const wsname = wb.SheetNames[0];
      const ws = wb.Sheets[wsname];
      const data = XLSX.utils.sheet_to_json(ws);
      const programs = data.map((row) => ({
        name: row["Name"],
        organization: row["Organization"],
        description: row["Description"],
        field: JSON.parse(row["Field"]),
        eligibility: JSON.parse(row['Eligibility']).map(Number),
        type: row["Type"],
        cost: parseInt(row["Cost"], 10),
        location: row["Location"],
        virtual: row["Virtual"] === "yes",  // Converts "TRUE" or "FALSE" to boolean
        startDate: new Date(row["Start Date"]),
        endDate: new Date(row["End Date"]),
        website: row["Website"],
        applicationDeadline: new Date(row["Application Deadline"]),
      }));
      console.log(programs);

    //   // Send to backend
    //   fetch("/api/upload-programs", {
    //     method: "POST",
    //     headers: {
    //       "Content-Type": "application/json",
    //     },
    //     body: JSON.stringify({ programs }),
    //   })
    //   .then((res) => res.json())
    //   .then((data) => console.log(data))
    //   .catch((err) => console.error(err));
    };
    reader.readAsBinaryString(file);
  };

  return (
    <div>
      <input type="file" accept=".xlsx,.xls" onChange={handleFileChange} />
    </div>
  );
};

export default UploadSpreadsheet;
